DROP TABLE IF EXISTS Item_Table;
DROP TABLE IF EXISTS Food_Table;
DROP TABLE IF EXISTS Cat_Table;

CREATE TABLE Cat_Table (
cat_name VARCHAR(20) NOT NULL,
cat_power_level INT NULL,
cat_regular TINYINT NULL,
cat_fish_gift_factor INT NULL,
PRIMARY KEY (cat_name));

CREATE TABLE Food_Table (
food_name VARCHAR(20) NOT NULL,
food_preference INT NULL,
food_cat_name VARCHAR(20) NULL,
PRIMARY KEY (food_name, food_cat_name),
CONSTRAINT food_cat_name FOREIGN KEY (food_cat_name) REFERENCES Cat_Table (cat_name));

CREATE TABLE Item_Table (
item_name VARCHAR(50) NOT NULL,
item_preference INT NULL,
item_cat_name VARCHAR(20) NULL,
PRIMARY KEY (item_name, item_cat_name),
CONSTRAINT item_cat_name FOREIGN KEY (item_cat_name) REFERENCES Cat_Table (cat_name));



